import { Input, Select } from 'antd'
import React, { useState } from 'react'
import {nanoid} from 'nanoid';
const BagComponent = () => {
    const [totalBag, setTotalBag] = useState([
        {
            id : 1,
        }
    ])
    const addNewBag = (e) =>{
        const item = { id : nanoid() }
        setTotalBag([...totalBag , item])
    }
    console.log(totalBag);
    return (
        <div>
            <button className="btn btn-primary w-100 mb-3" onClick={ () => addNewBag()}>Thêm bao</button>
            {
                totalBag.map((item,index) => <FormCreateBag key = {index} item = {item}  totalBag= {totalBag} setTotalBag={setTotalBag}/>)
            }
        </div>
    )
}

const FormCreateBag = ({item , setTotalBag ,totalBag}) => {
    const [valueForm, setValueForm] = useState({
        type : 1 ,
        kilo : 20 ,
        size : 1 ,
    })

    const changeType = (e) => {
    //    setValueForm({...valueForm , type : value } )
    //    const index =  totalBag.findIndex(bag => bag.id  === item.id)
    //    totalBag[index].type = value
    //    console.log(value);
    //    setTotalBag(totalBag)
    console.log(e);
    }
    const changeKilo = value =>{
        console.log(value);
       setValueForm({...valueForm , kilo : value } )
    }
    return <div className="row">
                <div className="col-4">
                    <Select
                        size="large"
                        defaultValue={1}
                        onChange={()=>changeType()}
                    >
                        {BAG_LIST.map((item) => (
                            <Select.Option key={item.id} value={item.id}>
                                {item.title}
                            </Select.Option>
                        ))}
                    </Select>
                </div>
                <div className="col-4">
                    <Select
                        size="large"
                        defaultValue={2}
                        onChange={()=>changeKilo()}
                        style ={{ width : '100px'}}
                    >
                        {BAG_KILO_LIST.map((item) => (
                            <Select.Option key={item.id} value={item.id}>
                                {item.title}
                            </Select.Option>
                        ))}
                    </Select>
                </div>
                <div className="col-4">
                    <Input
                    placeholder='Số bao' 
                    size='large'
                    value = {valueForm.size}
                    onChange={e => setValueForm({...valueForm , size : e.target.value})}
                    />
                </div>
            </div>
}
export default BagComponent
const BAG_LIST = [
    {
        id: 1,
        title: 'Lớn'
    },
    {
        id: 2,
        title: 'Trung'
    },
    {
        id: 3,
        title: 'bi'
    }
]
const BAG_KILO_LIST = [
    {
        id: 1,
        value: 30,
        title: '30kg'
    },
    {
        id: 2,
        value: 20,
        title: '20kg'
    },
    {
        id: 3,
        value: 10,
        title: '10kg'
    }
]
