import { Table } from 'antd'
import Modal from 'antd/lib/modal/Modal'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getListBuy } from '../actions/buyAction'
import FormCreateBuy from './form/FormCreateBuy'
import { TableBuy } from './table/TableBuy'

export const BuyOnion = () => {
    const [isModalCreate, setIsModalCreate] = useState(false)
    const [detailBuy, setDetailBuy] = useState(null)
    const dispatch = useDispatch()
    const { listBuy } = useSelector(state => state.buy)
    useEffect(() => {
        dispatch(getListBuy())
    }, [])
    const handleCancelCreate = () =>{   
        setIsModalCreate(false)
        setDetailBuy(null)
    }

    return (
        <div className="card mb-2 pb-2">
            <div className="d-flex justify-content-between mb-2">
                <div>
                    <span className="title-card"> Quản lí Thu Hành</span>
                </div>
                <div className="align-items-center">
                    <button
                        type="button"
                        className="btn btn-danger"
                        style={{minWidth: "110px"}}
                        onClick={()=>setIsModalCreate(true)}
                    >
                        <i className="fas fa-user-plus btn-icon"></i>Thêm
                    </button>
                </div>
            </div>

            <Modal  title="Tạo Đơn Mua Hàng" 
                    onCancel={handleCancelCreate} 
                    visible={isModalCreate} footer={null}>
                <FormCreateBuy
                    handleCancel={handleCancelCreate}
                />
            </Modal>
