import React, {useEffect, useState} from "react";
import {Form, Input, Select} from "antd";
import {useDispatch} from "react-redux";
import { createBuy } from "../../actions/buyAction";

const FormCreateBuy = (props) => {
    const {handleCancel, handleOk, detailMember, detailBranch} = props;
    const [formCreate] = Form.useForm();
    const dispatch = useDispatch();
    // useEffect(() => {
    //     if (detailMember) {
    //         formCreate.setFieldsValue({
    //             staff_code: detailMember.staff_code,
    //             staff_name: detailMember.staff_name,
    //             staff_username: detailMember.staff_username,
    //             role_id: detailMember.staff_role,
    //         });
    //     } else {
    //         formCreate.setFieldsValue({
    //             staff_code: null,
    //             staff_name: null,
    //             staff_username: null,
    //             role_id: ROLE_LIST[0].id,
    //         });
    //     }
    // }, [detailMember]);
    const onSubmit = () => {
        formCreate.submit();
    };

    const createSubmit = (info) => {
        console.log(info);
        dispatch(
            createBuy(info)
        );
    };
    const editSubmit = (info) => {
        // dispatch(

        // );
    };
    const handleFinishFormCreate = (info) => {
        // if (detailMember) {
        //     editSubmit(info);
        // } else {
        //     createSubmit(info);
        // }
        createSubmit(info)
        handleOk();
        formCreate.resetFields();
    };
    return (
        <div className="card">
            <Form
                form={formCreate}
                onFinish={handleFinishFormCreate}
                name="formCreat"
                // initialValues={{
                //     role_id: detailMember?.staff_role,
                // }}
            >
                <div className="row">
                    <div className="col-12">
                        <Form.Item
                            name="full_name"
                        >
                            <div className="manage-select" style={{marginBottom: "10px"}}>
                                <div>Tên Chủ Vườn :</div>
                                <Input
                                    size="large"
                                />
                            </div>
                        </Form.Item>
                    </div>
                    <div className="col-12">
                        <div className="manage-select" style={{marginBottom: "10px"}}>
                        <div>Số điện thoại:</div>
                        <Form.Item
                                name="phone_number"
                            >
                                <Input size="large"/>
                            </Form.Item>
                        </div>
                    </div>

                    <div className="col-12">
                        <div className="manage-select" style={{marginBottom: "10px"}}>
                            <div>Trạng thái :</div>
                            <Form.Item 
                                name="status"
                                >
                                <Select
                                    size="large"

                                >
                                    {ROLE_LIST.map((item) => (
                                        <Select.Option key={item.id} value={item.id}>
                                            {item.title}
                                        </Select.Option>
                                    ))}
                                </Select>
                            </Form.Item>
                        </div>
                    </div>

                    <div className="col-12">
                        <div className="d-flex mt-3 justify-content-center">
                            <button
                                type="button"
                                className="btn btn-danger mr-2"
                                onClick={onSubmit}
                            >
                                Lưu
                            </button>
                            <button
                                type="button"
                                className="btn btn-outline-danger"
                                onClick={handleCancel}
                            >
                                Huỷ
                            </button>
                        </div>
                    </div>
                </div>

            </Form>
        </div>
    );
};
export default FormCreateBuy;

const ROLE_LIST = [
    {
        id : 1 , 
        title : 'Đã mua'
    },
    {
        id : 2 , 
        title : 'Đã trả hết'
    },
    {
        id : 3 , 
        title : 'Đã còn nợ'
    },
]
