import React, {useEffect, useState} from "react";
import {Form, Input, Select} from "antd";
import {useDispatch} from "react-redux";
import { createBuy } from "../../actions/buyAction";
import TextArea from "antd/lib/input/TextArea";

const FormCreateBuy = (props) => {
    const {handleCancel, detailBuy ,setDetailBuy} = props;
    const [formCreate] = Form.useForm();
    const dispatch = useDispatch();
    // useEffect(() => {
    //     if (detailMember) {
    //         formCreate.setFieldsValue({
    //             staff_code: detailMember.staff_code,
    //             staff_name: detailMember.staff_name,
    //             staff_username: detailMember.staff_username,
    //             role_id: detailMember.staff_role,
    //         });
    //     } else {
    //         formCreate.setFieldsValue({
    //             staff_code: null,
    //             staff_name: null,
    //             staff_username: null,
    //             role_id: ROLE_LIST[0].id,
    //         });
    //     }
    // }, [detailMember]);
    const onSubmit = () => {
        formCreate.submit();
    };

    const createSubmit = (info) => {
        dispatch(
            createBuy(info)
        );
    };
    const editSubmit = (info) => {
        // dispatch(

        // );
    };
    const handleFinishFormCreate = (info) => {
        info.area = parseFloat(info.area)
        info.deposit = parseFloat(info.deposit)
        if (detailBuy) {
            editSubmit(info);
        } else {
            createSubmit(info);
        }
        formCreate.resetFields();
    };
    return (
        <div className="card">
            <Form
                form={formCreate}
                onFinish={handleFinishFormCreate}
                name="formCreat"
                initialValues={{
                    full_name: detailBuy?.full_name,
                    phone_number: detailBuy?.phone_number,
                    status: detailBuy?.status,
                    deposit: detailBuy?.deposit,
                    area: detailBuy?.area,
                    type_buy: detailBuy?.type_buy,
                }}
            >
                <div className="row">
                    <div className="col-12">
                        <div>Tên Chủ Vườn :</div>
                        <Form.Item
                                name="full_name"
                            >
                            <Input size="large"/>
                        </Form.Item>
                    </div>
                    <div className="col-6">
                        <div>Số điện thoại:</div>
                        <Form.Item
                                name="phone_number"
                            >
                            <Input size="large"/>
                        </Form.Item>
                    </div>

                    <div className="col-6">
                        <div>Trạng thái :</div>
                        <Form.Item 
                            name="status"
                            >
                            <Select
                                size="large"

                            >
                                {ROLE_LIST.map((item) => (
                                    <Select.Option key={item.id} value={item.id}>
                                        {item.title}
                                    </Select.Option>
                                ))}
                            </Select>
                        </Form.Item>
                    </div>

                    <div className="col-6">
                        <div>Tiền cọc:</div>
                        <Form.Item
                                name="deposit"
                            >
                            <Input size="large"/>
                        </Form.Item>
                    </div>
                    <div className="col-6">
                        <div>Diện tích :</div>
                        <Form.Item
                                name="area"
                            >
                            <Input size="large"/>
                        </Form.Item>
                    </div>
                    <div className="col-12">
                        <div>Hình thức :</div>
                        <Form.Item 
                            name="type_buy"
                            >
                            <Select
                                size="large"

                            >
                                {TYPE_BUY.map((item) => (
                                    <Select.Option key={item.id} value={item.id}>
                                        {item.title}
                                    </Select.Option>
                                ))}
                            </Select>
                        </Form.Item>
                    </div>
                    <div className="col-12">
                        <div>Ghi chú :</div>
                        <Form.Item
                                name="note"
                            >
                            <TextArea size="large"/>
                        </Form.Item>
                    </div>

                    <div className="col-12">
                        <div className="d-flex mt-3 justify-content-center">
                            <button
                                type="button"
                                className="btn btn-danger me-2"
                                onClick={onSubmit}
                            >
                                Lưu
                            </button>
                            <button
                                type="button"
                                className="btn btn-outline-primary"
                                onClick={handleCancel}
                            >
                                Huỷ
                            </button>
                        </div>
                    </div>
                </div>

            </Form>
        </div>
    );
};
export default FormCreateBuy;

export const ROLE_LIST = [
    {
        id : 1 , 
        title : 'Đã mua'
    },
    {
        id : 2 , 
        title : 'Đã trả hết'
    },
    {
        id : 3 , 
        title : 'Đã còn nợ'
    },
]

export const TYPE_BUY = [
    {
        id : 1 , 
        title : 'Mua xào'
    },
    {
        id : 2 , 
        title : 'Mua kí'
    },
]
