import {  LOGIN_FAIL, LOGIN_REQUEST, LOGIN_SUCCESS } from '../constants/actionTypes';
import * as api from '../api/index.js';
import { showNotification } from '../helper';

export const loginAccount = (user) => async (dispatch) => {
  try {
    dispatch({ type: LOGIN_REQUEST });
    const { data } = await api.login(user);
    console.log("alo" ,  data);
    const {success , message } = data
    if(success){
      dispatch({ type: LOGIN_SUCCESS , payload : message });
      showNotification({type: 'Succes', message: message, title: "Login"})
    }
  } catch (error) {
      dispatch({ type: LOGIN_FAIL });
      showNotification({type: 'error', message: "Error", title: "Error"})
  }
};
