import { Input, Select } from 'antd'
import React, { useState } from 'react'

const BagComponent = () => {
    const [totalBag, setTotalBag] = useState(1)
    return (
        <div>
            <button className="btn btn-primary w-100" onClick={ () => setTotalBag( totalBag + 1)}>Thêm bao</button>
            {
                totalBag.map((item,index) => <FormCreateBag/>)
            }
        </div>
    )
}

const FormCreateBag = () => {
    return <div className="row">
    <div className="col-4">
        <Select
            size="large"
            defaultValue={1}

        >
            {BAG_LIST.map((item) => (
                <Select.Option key={item.id} value={item.id}>
                    {item.title}
                </Select.Option>
            ))}
        </Select>
    </div>
    <div className="col-4">
        <Select
            size="large"
            defaultValue={2}
            style ={{ width : '100px'}}
        >
            {BAG_KILO_LIST.map((item) => (
                <Select.Option key={item.id} value={item.id}>
                    {item.title}
                </Select.Option>
            ))}
        </Select>
    </div>
    <div className="col-4">
        <Input
           placeholder='Số bao' 
           size='large'
         />
    </div>
</div>
}
export default BagComponent
const BAG_LIST = [
    {
        id: 1,
        title: 'Lớn'
    },
    {
        id: 2,
        title: 'Trung'
    },
    {
        id: 3,
        title: 'bi'
    }
]
const BAG_KILO_LIST = [
    {
        id: 1,
        value: 30,
        title: '30kg'
    },
    {
        id: 2,
        value: 20,
        title: '20kg'
    },
    {
        id: 3,
        value: 10,
        title: '10kg'
    }
]
